/* ################################### Header ################################### */
.header{
	display: block;
	border-bottom: 1px solid #e2e2e2;
	font-size: 0;
    background-color: white;
    top: 0;
    position: fixed;
    width: 100%;
}
.header .header-logo{
	width: 15%;
	padding: 10px 0px;
	box-sizing: border-box;
}
.header .header-logo img{
	width: auto;
}
.header .header-menu{
	width: 45%;
	padding: 0px 0px;
	box-sizing: border-box;
}
.header .header-menu ul{
	display: block;
	margin: 0 0;
	padding: 14px 0;
}
.header .header-menu ul li{
	display: inline-block;
	margin: 0 10px;
	padding: 0 0;
	font-size: 13px;
}
.header .header-menu ul li a{
	color: #666;
}
.header .header-menu ul li a:hover{
	text-decoration: none;
	color: #000;
}
.header .header-search{
	width: 10%;
	padding: 15px 0px;
	box-sizing: border-box;
	border-left: 1px solid #e2e2e2;
	border-right: 1px solid #e2e2e2;
	text-align: center;
}
.header .header-search p{
	display: block;
	margin: 0 0;
	padding: 0 0;
	font-size: 13px;
	color: #666;
}
.header .header-social{
	width: 15%;
	padding: 15px 5px;
	box-sizing: border-box;
	border-right: 1px solid #e2e2e2;
}
.header .header-social ul{
	display: block;
	margin: 0 auto;
	padding: 0 0;
	text-align: center;
}
.header .header-social ul li{
	display: inline-block;
	font-size: 14px;
	margin: 0 5px;
}
.header .header-social ul li a{
	color: #666;
}
.header .header-social ul li a:hover{
	text-decoration: none;
	color: #000;
}
.header .header-shop{
	width: 10%;
	padding: 12px 0px;
	box-sizing: border-box;
	color: #666;
	text-align: center;
	border-right: 1px solid #e2e2e2;
}
.header .header-shop p{
	display: block;
	font-size: 13px;
	margin: 0 0;
	padding: 0 0;
	text-transform: uppercase;
	font-weight: bold;
}
.header .header-shop p i{
	color: #fff;
	background-color: #f5a623;
	border-radius: 50%;
	padding: 4px 5px;
}
.header .header-prof{
	width: 5%;
	padding: 15px 0px;
	box-sizing: border-box;
	text-align: center;
}
.header .header-prof i{
	display: block;
	margin: 0 0;
	padding: 0 0;
	font-size: 13px;
	color: #666;
}
/* ################################### Content ################################### */
.content{
	display: block;
	margin: 50px 0;
	font-size: 0;
}
.content .article{
	width: 70%;
	padding: 0px 0px;
	box-sizing: border-box;
}
.content .article h1{
	display: block;
	margin: 10px 0;
	padding: 0 0;
	font-size: 32px;
	line-height: 38px;
	font-weight: normal;
}
.content .article h2{
	display: block;
	margin: 14px 0;
	padding: 0 0;
	font-size: 25px;
	line-height: 30px;
    font-weight: bold;
}
.content .article h3{
	display: block;
	margin: 10px 0;
	padding: 0 0;
	font-size: 20px;
	line-height: 25px;
}
.content .article p{
	display: block;
	margin: 10px 0;
	padding: 0 0;
	font-size: 18px;
	line-height: 30px;
}

.content .article ul{
    list-style-type: circle;
    display: block;
	font-size: 18px;
	line-height: 24px;
}
.content .article p.cat{
	display: block;
	margin: 10px 0;
}
.content .article p.cat span{
	padding: 0px 0px 5px;
	border-bottom: 2px solid #e2e2e2;
}
.content .article .meta{
	display: block;
	margin: 20px 0;
	font-size: 0;
}
.content .article .meta .meta-left{
	width: 50%;
	padding: 0 0;
	box-sizing: border-box;
}
.content .article .meta .meta-left img{
	display: inline-block;
	margin: 0 5px;
	vertical-align: top;
	width: 30px;
}
.content .article .meta .meta-left p{
	display: inline-block;
	margin: 2px 0;
	font-size: 14px;
	line-height: 15px;
}
.content .article .meta .meta-left p span{
	display: block;
	text-transform: uppercase;
}
.content .article .meta .meta-right{
	width: 50%;
	padding: 0 0;
	box-sizing: border-box;
	text-align: right;
}
.content .article .meta .meta-right p{
	display: block;
	text-align: right;
	font-size: 15px;
}
.content .article .videoWrapper {
    height: 0;
    margin: 30px 0;
    padding-bottom: 56.25%;
    padding-top: 25px;
    position: relative;
}
.content .article .videoWrapper iframe {
    height: 100%;
    left: 0;
    position: absolute;
    top: 0;
    width: 100%;
}
.content .article .inst{
	display: block;
	width: 80%;
	margin: 30px auto;
}
.content .article h4{
	display: block;
	position: relative;
	text-align: center;
	font-size: 15px;
	text-transform: uppercase;
	font-weight: normal;
}
.content .article h4 span{
	padding: 0 20px;
	background-color: #fff;
	position: relative;
	z-index: 1;
}
.content .article h4::after{
	position: absolute;
	top: 50%;
	left: 0;
	right: 0;
	content: '';
	height: 1px;
	margin-top: -1px;
	background-color: #666;
}
.content .article .more{
	display: block;
	margin: 20px 0;
	font-size: 0;
}
.content .article .more .more-box{
	width: 25%;
	padding: 0 5px;
	box-sizing: border-box;
}
.content .article .more .more-box p{
	display: block;
	font-size: 15px;
	line-height: 18px;
}
.content .article .more .more-box a{
	color: #666;
}
.content .article img.share{
	display: block;
	width: auto;
	margin: 20px 0;
}
.content .article .grey_box{
	width: 100%;
	background-color: #ADD8E6;
