/* ################################### Header ################################### */
.header{
	display: block;
	border-bottom: 1px solid #e2e2e2;
	font-size: 0;
    background-color: white;
    top: 0;
    position: fixed;
    width: 100%;
}
.header .header-logo{
	width: 15%;
	padding: 10px 0px;
	box-sizing: border-box;
}
.header .header-logo img{
	width: auto;
}
.header .header-menu{
	width: 45%;
	padding: 0px 0px;
	box-sizing: border-box;
}
.header .header-menu ul{
	display: block;
	margin: 0 0;
	padding: 14px 0;
}
.header .header-menu ul li{
	display: inline-block;
	margin: 0 10px;
	padding: 0 0;
	font-size: 13px;
}
.header .header-menu ul li a{
	color: #666;
}
.header .header-menu ul li a:hover{
	text-decoration: none;
	color: #000;
}
.header .header-search{
	width: 10%;
	padding: 15px 0px;
	box-sizing: border-box;
	border-left: 1px solid #e2e2e2;
	border-right: 1px solid #e2e2e2;
	text-align: center;
}
.header .header-search p{
	display: block;
	margin: 0 0;
	padding: 0 0;
	font-size: 13px;
	color: #666;
}
.header .header-social{
	width: 15%;
	padding: 15px 5px;
	box-sizing: border-box;
	border-right: 1px solid #e2e2e2;
}
.header .header-social ul{
	display: block;
	margin: 0 auto;
	padding: 0 0;
	text-align: center;
}
.header .header-social ul li{
	display: inline-block;
	font-size: 14px;
	margin: 0 5px;
}
.header .header-social ul li a{
	color: #666;
}
.header .header-social ul li a:hover{
	text-decoration: none;
	color: #000;
}
.header .header-shop{
	width: 10%;
	padding: 12px 0px;
	box-sizing: border-box;
	color: #666;
	text-align: center;
	border-right: 1px solid #e2e2e2;
}
.header .header-shop p{
	display: block;
	font-size: 13px;
	margin: 0 0;
	padding: 0 0;
	text-transform: uppercase;
	font-weight: bold;
}
.header .header-shop p i{
	color: #fff;
	background-color: #f5a623;
	border-radius: 50%;
	padding: 4px 5px;
}
.header .header-prof{
	width: 5%;
	padding: 15px 0px;
	box-sizing: border-box;
	text-align: center;
}
.header .header-prof i{
	display: block;
	margin: 0 0;
	padding: 0 0;
	font-size: 13px;
	color: #666;
}
/* ################################### Content ################################### */
.content{
